	&lt;li style=&quot;box-sizing: border-box;&quot;&gt;kolay kurululudur&lt;/li&gt;
	&lt;li style=&quot;box-sizing: border-box;&quot;&gt;Tabanı ve tavanı su geçirmez&lt;/li&gt;
	&lt;li style=&quot;box-sizing: border-box;&quot;&gt;Pratik taşıma çantalı&lt;/li&gt;
	&lt;li style=&quot;box-sizing: border-box;&quot;&gt;Cibinlikli&lt;/li&gt;
	&lt;li style=&quot;box-sizing: border-box;&quot;&gt;Ölçüleri 200*150*110&lt;/li&gt;
